class Solution {
public:
    int minimumMountainRemovals(vector<int>& nums) {
        int n = nums.size();
        vector<int> dp1(n,1);  //to calculate LIS from the left side

        int maxi = 1;

        for(int i=1; i<=n-1; i++){

            for(int prev_index = 0; prev_index <= i-1; prev_index ++){

                if( nums[i]>nums[prev_index] && 1 + dp1[prev_index] > dp1[i]){
                    dp1[i] = 1 + dp1[prev_index]; //dp1[i] stores the length of LIS till the ith index from left
                }
            }
        }

        vector<int> dp2(n,1);   //to calculate LIS from the right side
        for(int i=n-2; i>=0; i--){

            for(int prev_index = n-1; prev_index >i; prev_index --){

                if( nums[i]>nums[prev_index] && 1 + dp2[prev_index] > dp2[i]){ 
                    dp2[i] = 1 + dp2[prev_index]; //dp2[i] stores the length of LIS till the ith index from right
                }
            }
        }

        for(int i=1;i<n-1;i++){
            if(dp1[i]>1&&dp2[i]>1)    //validity of peak
                maxi=max(maxi,dp1[i]+dp2[i]-1);    // -1 because the middle element(peak) gets considered twice
        }

        return n-maxi;   //maxi follows the mountain array so we need to remove the rest elements

    }
};
