class Solution {
public:
    int minimumMountainRemovals(vector<int>& nums) {
        int n = nums.size();
        vector<int> dp(n+1, 1000000000);
        vector<int> LIS1(n,0);
        vector<int> LIS2(n,0);
        for(int i = 0;i<n;++i)
        {
            int idx = lower_bound(dp.begin(),dp.end(),nums[i])-dp.begin(); // seach for the correct position for nums[i]
            LIS1[i] = idx+1; // current LIS ending with index i is idx +1 as lower_bound is 0 based 
            dp[idx] = nums[i]; //override the element at dp[idx] with nums[i] 
        }
        fill(dp.begin(),dp.end(),1000000000); // re-fill our dp array with maximum value
        for(int i = n-1;i>=0;--i) // instead of LDS we can apply the same logic above which is LIS but starting from the end of the array
        {
            int idx = lower_bound(dp.begin(),dp.end(),nums[i])-dp.begin(); 
            LIS2[i] = idx+1;
            dp[idx] = nums[i];
        }
        int ans = 0;
        for(int i = 0;i <n;++i)
            if(LIS1[i]>=2 && LIS2[i]>=2) // we are assuming that this index is the current peak but if one of the peaks length is 1 so it can't be a peak 
                ans = max(ans,LIS1[i]+LIS2[i]-1);

        return n-ans; // we found the LIS so we need to subtract it from the length of the total numbers to get the mountain
    }
};
