public:
    int minimumMountainRemovals(vector<int>& a) {
        int n = a.size();
        vector<int> dp1(n, 1);          
        //LIS form front
        for(int i=0; i<n; i++){
            for(int prev=0; prev<i; prev++){
                if(a[i]>a[prev] && dp1[i] < 1 + dp1[prev] ){
                    dp1[i] = 1 + dp1[prev];
                }
            }
        }

        vector<int> dp2(n, 1);          
        //LIS form back or LDS
        for(int i = n-1; i>=0; i--){
            for(int prev = n-1; prev>i; prev--){
                if(a[i]>a[prev] && dp2[i] < 1 + dp2[prev] ){
                    dp2[i] = 1 + dp2[prev];
                }
