class Solution {
public:
    int helper(int arr[],int l,int r,int x){
        while(r>l){
            int m= l+(r-l)/2;
            if(arr[m]>=x){
                r=m;
            }
            else{
                l=m+1;
            }
        }
        return r;
    }
    int he(vector<int>nums,vector<int>&dp){
