public int minimumMountainRemovals(int[] nums) {

        int len = nums.length;

        //longest increasing subsequence (LIS)
        int[] lis = new int[len];
        //longest decreasing subsequence (LDS)
        int[] lds = new int[len];

        //1. build longest increasing subsequence
        for(int i = 0; i < len; i++){
            lis[i] = 1;
            for(int j = 0; j < i; j++){
                if(nums[i] > nums[j]){
                    lis[i] = Math.max(lis[i], lis[j]+1);
                }
            }
        }

        //2. build longest decreasing subsequence
        for(int i = len-1; i>=0; i--){
            lds[i] = 1;
            for(int j = len-1; j > i; j--){
                if(nums[i] > nums[j]){
                    lds[i] = Math.max(lds[i], lds[j]+1);
                }
            }
        }

        int mountainLen = 0;
        for(int i = 0; i < len; i++){
            if(lis[i] > 1 && lds[i] > 1){
                //let's image current i as the peak of mountain
                //if lis[i] > 1, it means there are elements on the left slope of mountain
                //if lds[i] > 1, it means there are elements on the right slope of mountain
                //so current total number of mountain elements should be lis[i] + lds[i] - 1
                //since i are both included in lis[i] and lds[i], we need to minus one
                mountainLen = Math.max(mountainLen, lis[i]+lds[i]-1);
            }
        }
        return len - mountainLen;
    }
