def minimumMountainRemovals(nums):
	dp_begin = [1]*len(nums)
	dp_end = [1]*len(nums)
	for i in range(len(nums)):
		for j in range(i+1):
			if nums[j] < nums[i]:
				dp_begin[i] = max(dp_begin[j]+1, dp_begin[i])

	maxi = 0
	for i in range(len(nums)):
		maxi = max(maxi, dp_begin[i])
		dp_begin[i] = maxi

	for i in range(len(nums)-1, -1, -1):
		for j in range(i+1, len(nums)):
			if nums[j] < nums[i]:
				dp_end[i] = max(dp_end[j]+1, dp_end[i])
	maxi = 0
	for i in range(len(nums)-1, -1, -1):
		maxi = max(maxi, dp_end[i])
		dp_end[i] = maxi

	best = 0
	for i in range(1, len(dp_begin)-1):
		if dp_begin[i] != 1 and dp_end[i] != 1:
			best = max(best, dp_begin[i]+dp_end[i])

	return len(nums)-best+1
