class Solution {
    public int minimumMountainRemovals(int[] A) {
        int n = A.length;
        int[] inc = new int[n], dec = new int[n], dp = new int[n];
        int size = 0, idx = 0;
        for(int x : A){
            int i = binarySearch(dp, x, size);
            dp[i] = x;
            if(i == size) size++;
            inc[idx++] = size;
        }

        dp = new int[n];
        size = 0;
        for(idx = n - 1; idx >= 0; idx--){
            int x = A[idx];
            int i = binarySearch(dp, x, size);
            dp[i] = x;
            if(i == size) size++;
            dec[idx] = size;
        }

        int max = 0;
        for(int i = 0; i < n; i++){
            if(inc[i] != 1 && dec[i] != 1)
            max = Math.max(max, inc[i] + dec[i] - 1);
        }

        return n - max;
    }

    int binarySearch(int[] dp, int x, int size){
        int i = 0, j = size - 1;
        while(i <= j){
            int m = (j+i)/2;
            if(dp[m] < x) i = m + 1;
            else j = m - 1;
        }
        return i;
    }
}
