// O(n^2) O(n)
public int minimumMountainRemovals1(int[] nums) {

	int len = nums.length, omax = Integer.MIN_VALUE;
	int[] dp1 = new int[len], dp2 = new int[len];

	for (int i = 0; i < len; i++) {
		int msf = 0;
		for (int j = 0; j < i; j++) {
			if (nums[i] > nums[j] && dp1[j] > msf)
				msf = dp1[j];
		}
		dp1[i] = msf + 1;
	}

	for (int i = len - 1; i >= 0; i--) {
		int msf = 0;
		for (int j = i + 1; j < len; j++) {
			if (nums[i] > nums[j] && dp2[j] > msf)
				msf = dp2[j];
		}
		dp2[i] = msf + 1;
	}

	for (int i = 1; i < len - 1; i++) {

		int left = dp1[i], right = dp2[i];
		if (left > 1 && right > 1 && left + right - 1 > omax)
			omax = left + right - 1;
	}

	return len - omax;
}

// O(nlogn) O(n)
public int minimumMountainRemovals2(int[] nums) {

	int len = nums.length, omax = Integer.MIN_VALUE;
	int[] dp1 = longestIncreasingSubsequence(nums);
	int[] dp2 = longestDecreasingSubsequence(nums);

	for (int i = 1; i < len - 1; i++) {
		int left = dp1[i], right = dp2[i];
		if (left + right - 1 > omax)
			omax = left + right - 1;
	}

	return len - omax;
}

// O(nlogn) O(n)
public int[] longestDecreasingSubsequence(int[] nums) {

	int len = 1, length = nums.length;
	int[] ans = new int[length], arr = new int[length];

	arr[0] = nums[length - 1];
	ans[length - 1] = 1;

	for (int i = len - 2; i >= 0; i--) {

		int val = nums[i];
		if (val > arr[len - 1]) {
			arr[len++] = val;
			ans[i] = len;
		} else {
			int idx = binarySearch(arr, 0, len - 1, val);
			arr[idx] = val;
			ans[i] = idx + 1;
		}
	}

	return ans;
}

// O(nlogn) O(n)
public int[] longestIncreasingSubsequence(int[] nums) {

	int len = 1, length = nums.length;
	int[] ans = new int[length], arr = new int[length];

	arr[0] = nums[0];
	ans[0] = 1;

	for (int i = 1; i < length; i++) {

		int val = nums[i];
		if (val > arr[len - 1]) {
			arr[len++] = val;
			ans[i] = len;
		} else {
			int idx = binarySearch(arr, 0, len - 1, val);
			arr[idx] = val;
			ans[i] = idx + 1;
		}
	}

	return ans;
}

// O(logn) O(1)
public int binarySearch(int[] arr, int lo, int hi, int val) {

	int ans = -1;
	while (lo <= hi) {

		int mid = (lo + hi) / 2;
		if (val <= arr[mid]) {
			ans = mid;
			hi = mid - 1;
		} else
			lo = mid + 1;
	}

	return ans;
}

