def minimumMountainRemovals(self, nums: List[int]) -> int:
	n = len(nums)

    def lis(nums):
		longest = []
        length = [1] * n
        for i, num in enumerate(nums):
			index = bisect.bisect_left(longest, num)
            if index == len(longest):
				longest.append(num)
			else:     
				longest[index] = num
			length[i] = index
		return length

	left = lis(nums)
    right = lis(nums[::-1])[::-1]

    max_length = 1    
    for i in range(1, n - 1):
		if left[i] and right[i]:
			max_length = max(max_length, left[i] + right[i] + 1)
	return n - max_length
