class Solution {
public:

    vector<int> lis(vector<int> v)
    {
        int i,n=v.size();
        vector<int> dp(n,1);
        for(int i=1;i<n;i++)
        {
            for(int j=0;j<i;j++)
            {
                if((v[j]<v[i]))
                {
                    dp[i]=max(dp[i],dp[j]+1);
                }
            }
        }
        return dp;
    }


    int minimumMountainRemovals(vector<int>& nums) {
        vector<int> a1=lis(nums);
        reverse(nums.begin(),nums.end());
        vector<int> a2=lis(nums);
        reverse(a2.begin(),a2.end());
        reverse(nums.begin(),nums.end());
        int n=nums.size(),an=0;
        for(int i=0;i<n;i++)
        {
            if((a1[i]==1)||(a2[i]==1))
            {
                continue;
            }
            an=max(an,(a2[i]+a1[i]-1));
        }
        return (n-an);
    }
};
