            if (it!=st.end()){
                 int idx = it- st.begin();
                st[idx] = nums[i];
                right[n-1-i] = idx+1;
            }else{
                st.push_back(nums[i]);
                right[n-1-i] = st.size();
            }
        }

        int res = INT_MAX;
        for (int i =1;i<n-1;i++){
            if (left[i]>1 && right[i]>1){
                res = min(res,i+1-left[i]+n-i-right[i]);
            }

        }
