// this is same as longest bitonic subsequence where bitonic sequences are the sequences that increase first then decrease after some time
// so we find the maximum longest bitonic subsequence and subtract the total array length. This will give the element to be removed.

class Solution {
    public int minimumMountainRemovals(int[] array) {
        int n = array.length;
        int [] lis = new int [n];
        int [] lds = new int [n];
        for (int i=0; i<n; i++) {
            lis[i] = 1;
            lds[i] = 1;
        }
        for (int i=1; i<n; i++) {
            for (int j=0; j<i; j++) {
                if (array[i] > array[j] && lis[i] < lis[j] + 1)
                    lis[i] = lis[j] + 1;
            }
        }
        for (int i=n-2; i>=0; i--) {
            for (int j=n-1; j>i; j--) {
                if (array[i] > array[j] && lds[i] < lds[j] + 1)
                    lds[i] = lds[j] + 1;
            }
        }
        int max = 0;
        for (int i=1; i<n-1; i++) {
            if (max < lis[i] + lds[i] - 1)
                max = lis[i] + lds[i] - 1;
        }
        return n - max;
    }
}
