    for(int i = 0; i < n; ++i)
        a[i] = b[i] = 1;
    for(int i = 0; i < n ; ++i){
        for(int j = 0; j < i; ++j)
            if(nums[j] < nums[i]){
                a[i] = max(a[i], a[j] + 1);
            }
    }
    for(int i = n-1; i >=0 ; --i){
        for(int j = n-1; j > i; --j){
            if(nums[j] < nums[i]){
                b[i] = max(b[i],b[j]+1);
            }
        }
    }
    int ans = 0;
    for(int i = 1; i < n-1; ++i)
        ans = max(ans,a[i]+b[i]);
    return n - ans + 1;
}
