class Solution {
    public int minimumMountainRemovals(int[] nums) {
        int n = nums.length;  

        // Using left dp to find out the min elements to be removed for each index.
        int []left = new int[n];
        for(int i=0; i<n; i++) {
            int minRequired = i;
            for(int j=0; j<i; j++) {
                if(nums[j] < nums[i]) {
                    minRequired = Math.min(minRequired, i - j - 1 + left[j]);
                }
            }
            left[i] = minRequired;
        }

         // Using right dp to find out the min elements to be removed for each index.
        int []right = new int[n];
        Arrays.fill(right, 0);
        for(int i= n-1; i>=0; i--) {
            int minRequired = n - i - 1;
            for(int j=n-1; j>i; j--) {
                if(nums[j] < nums[i]) {
                    minRequired = Math.min(minRequired, j - i - 1 + right[j]);
                }
            }
            right[i] = minRequired;
        }


        // Exclude if any element after removal becomes first or last. 
        int minRemovals = n;
        for(int i=1; i<n-1; i++) {
            if(left[i] != i && right[i] != n - i -1) {
                minRemovals = Math.min(minRemovals, left[i] + right[i]);
            }
        }

        return minRemovals;

    }
}
