class Solution {
    public int minimumMountainRemovals(int[] nums) {
        int[] left = new int[nums.length];
        int[] right = new int[nums.length];
        List<Integer> lis = new ArrayList<>();
        for (int i = 0; i < nums.length; i++) {
            left[i] = lengthOfLIS(lis, nums[i]);
        }

        lis.clear();
        for (int i = nums.length - 1; i >= 0; i--) {
            right[i] = lengthOfLIS(lis, nums[i]);
        }

        int ans = 0;
        for (int i = 0; i < nums.length; i++) {
            if (left[i] >= 2 && right[i] >= 2) {
                int val = left[i] + right[i] - 1;
                ans = Math.max(ans, val);
            }
        }
        return nums.length - ans;
    }


    public int lengthOfLIS(List<Integer> sub, int num) {
        if (sub.isEmpty() || num > sub.get(sub.size() - 1)) {
            sub.add(num);
            return sub.size();
        } else {
            int j = binarySearch(sub, num);
            sub.set(j, num);
            return j + 1;
        }
    }

    private int binarySearch(List<Integer> sub, int num) {
        int left = 0;
        int right = sub.size() - 1;

        while (left < right) {
            int mid = (left + right) / 2;
            if (sub.get(mid) == num) {
                return mid;
            }

            if (sub.get(mid) < num) {
                left = mid + 1;
            } else {
                right = mid;
            }
        }

        return left;
    }
}
