class Solution {
public:
 int optimalSol(vector<int> & nums, vector<int> &lis){
        if(nums.size()==0){
            return 0;
        }
        vector<int> ans;
        lis.push_back(1);
        ans.push_back(nums[0]);
        for(int i = 1;i<nums.size();i++){
            if(nums[i]>ans.back()){
                ans.push_back(nums[i]);
                lis.push_back(ans.size());
            }else{
                int index = lower_bound(ans.begin(),ans.end(),nums[i])-ans.begin();
                ans[index] = nums[i];
                lis.push_back(index+1);
            }
        }
        return ans.size();
    }
    int minimumMountainRemovals(vector<int>& nums) {
        vector<int> lis, lds;
        optimalSol(nums,lis);
        reverse(nums.begin(),nums.end());
        optimalSol(nums,lds);


        int longestMountain = INT_MIN;
        for(int i = 0;i<nums.size();i++){
            if(lis[i] ==1  || lds[nums.size()-i-1]==1) continue;
            longestMountain = max(longestMountain,lis[i]+lds[nums.size()-i-1]-1);
        }
        int minNum = nums.size()-longestMountain;
        return minNum;
    }
};
