            dp[i]=1;
        }

        int maxi=-1;
        for(int i=0;i<nums.length;i++)
        {
            for(int j=0;j<i;j++)
            {
                if(nums[i]>nums[j])
                {
                    dp[i]=Math.max(dp[i],1+dp[j]);
                }
            }
            maxi=Math.max(maxi,dp[i]);
        }

        return dp;
