        int dp1[]=LIS(nums);
        int dp2_reverse[]=LIS(reverse_arr);
        int dp2[]=new int[nums.length];

        //Reverse dp2_reverse to align the elements to be added
        j=0;
        for(int i=nums.length-1;i>=0;i--)
