class Solution {
public:
    int minimumMountainRemovals(vector<int>& nums) {
        int size = nums.size();
        vector<int> lis1 (size), lis2 (size);
        vector<int> piles;
        for (int i = 0, left; i < size; i+=1) {
            int& x = nums[i];
            if (piles.empty() || x > piles.back()) {
                piles.emplace_back(x);
                lis1[i] = piles.size();
                continue;
            }
            left = lower_bound(piles.begin(), piles.end(), x) - piles.begin();
            lis1[i] = left + 1;
            piles[left] = x;
        }

        piles.clear();
        for (int i = size-1, left; i >= 0; i-=1) {
            int& x = nums[i];
            if (piles.empty() || x > piles.back()) {
                piles.emplace_back(x);
                lis2[i] = piles.size();
                continue;
            }
            left = lower_bound(piles.begin(), piles.end(), x) - piles.begin();
            lis2[i] = left + 1;
            piles[left] = x;
        }

        int ans = size + 1;
        for (int i = 1, removed; i < size - 1; i+=1) {
            if (lis1[i] == 1 or lis2[i] == 1) continue;
            removed = size - (lis1[i] + lis2[i] - 1);
            ans = ans < removed ? ans: removed;
        }
        return ans;
    }
};
