class Solution {
public:
    int minimumMountainRemovals(vector<int>& v) {
        int n=v.size();
        vector<int>left(n,0);
        vector<int>right(n,0);
        left[0]=1;
        for(int i=1;i<n;i++){
            int max=0;
            for(int j=0;j<i;j++){
                if(v[i]>v[j] and max<left[j]){
                    max=left[j];
                }
            }
            left[i]=max+1;
        }
        right[n-1]=1;
        for(int i=n-2;i>=0;i--){
            int max=0;
            for(int j=i+1;j<n;j++){
                if(v[i]>v[j] and max<right[j]){
                    max=right[j];
                }
            }
            right[i]=max+1;
        }
        int k=0;
        for(int i=1;i<n-1;i++){
            if(left[i]>1 and right[i]>1) k=max(k,right[i]+left[i]-1);
        }
        return n-k;
    }
};
