// The code below wasn't writen to be readable, it is for runtime optimization.
/**
 * @param {number[]} nums
 * @return {number}
 */
var minimumMountainRemovals = function (nums) {
  const n = nums.length;
  const nm1 = n - 1;
  const pre = Array(n).fill(0);
  const h = Array(n).fill(2e9);

  let i, l, r, m, p;
  h[0] = nums[0];
  for (i = 1; i < n - 1; i++) {
    l = 0;
    p = 0;
    r = i;

    while (l <= r) {
      m = (r + l) >> 1;

      if (h[m] < nums[i]) {
        l = m + 1;
        p = l;
      } else r = m - 1;
    }

    h[p] = nums[i];
    pre[i] = p;
  }

  let lis = 0;
  h.fill(2e9);
  h[0] = nums[nm1];
  for (i = n - 2; i > 0; i--) {
    l = 0;
    p = 0;
    r = nm1 - i;

    while (l <= r) {
      m = (r + l) >> 1;

      if (h[m] < nums[i]) {
        l = m + 1;
        p = l;
      } else r = m - 1;
    }

    h[p] = nums[i];
    if (p && pre[i]) lis = Math.max(lis, p + pre[i]);
  }

  return nm1 - lis;
};
