class Solution {
    public int minimumMountainRemovals(int[] nums) {
        return nums.length - longestBitonicSequence(nums);
    }

    public static int longestBitonicSequence(int[] nums) {
        int[] lis = lengthOfLIS(nums);
        reverse(nums);
        int[] lds = lengthOfLIS(nums);
        reverse(lds);
        int my_ans = 1;
        for (int i = 0; i < nums.length; i++){
            if(lis[i]>1 && lds[i]>1)
                my_ans = Math.max(my_ans, lis[i] + lds[i] - 1);
        }
        return my_ans;
    }

    public static void reverse(int[] nums) {
        int lo = 0, hi = nums.length - 1;
        while (lo < hi) {
            int temp = nums[lo];
            nums[lo] = nums[hi];
            nums[hi] = temp;
            lo++;
            hi--;
        }
    }

    public static int[] lengthOfLIS(int[] nums) {
        int n = nums.length;
        int[] lis = new int[n];
        Arrays.fill(lis, 1);
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < i; j++)
                if (nums[i] > nums[j])
                    lis[i] = Math.max(lis[i], lis[j] + 1);
        }
        return lis;
    }
}
