            if(flag==0){
                return -1e5;
            }
            return 0;
        }
        if(dp[i][j+1][k][flag]!=-1) return dp[i][j+1][k][flag];
        int pick=0;
        if(j==-1){
            pick=1+f(i+1,i,a,0,0);
        }

       else{
        if(k==0){
            if(a[i]>a[j]) pick=1+max(f(i+1,i,a,0,flag),f(i+1,i,a,1,flag));
        }
        if(k==1){
            if(a[i]<a[j]) pick=1+f(i+1,i,a,1,1);
        }
       }
         int nopick=0+f(i+1,j,a,k,flag);
        return dp[i][j+1][k][flag]=max(pick,nopick);


    }
    int minimumMountainRemovals(vector<int>& nums) {

        memset(dp,-1,sizeof(dp));
        int maxi=f(0,-1,nums,0,0);

         return nums.size()-maxi;

    }
};

