        graph = new ArrayList[n];

         for(int node = 0; node < graph.length; node++)
            graph[node] = new ArrayList<>();

        //build graph: poor -> rich
        for(int i = 0; i < richer.length; i++){
            graph[richer[i][1]].add(richer[i][0]);
