        int[] answer = new int[n];
        Arrays.fill(answer, -1);


        for(int i = 0; i < n; i++){
            dfs(i, answer, quiet);
        }
        return answer;

    }


    public int dfs(int node, int[] answer, int[] quiet){

        //filling result array with -1 initially will avoid repetion here
        if (answer[node] == -1){
            answer[node] = node;

            //depthwise check for every richer person if they are quieter
            for (int child: graph[node]){

                int curr = dfs(child, answer, quiet);

                //quieter person
                if (quiet[curr] < quiet[answer[node]])
                    answer[node] = curr;
            }
        }
        return answer[node];
    }
}

