class Solution {
public:
    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) {

        int n = quiet.size();

        // track Indegree count of nodes
        vector<int>indegree(n,0);

        // make adjacent list of nodes where a directed edge from richer to poorer person  
        vector<vector<int>>edges(n);

        // result vector containing answer
        vector<int>result(n);

        // Max priority queue of node and indegree count
        // Insert weight Indegree count as first element 
        priority_queue<pair<int,int>> pQ;

        // make default result each person as their own index
        for(int i=0;i<n;i++)
            result[i]=i;

        // populate the edgelist and update result based on result 
        // also count the indegree count of node richer[i][1] from righer vector
        for(int i=0;i<richer.size();i++)
        {
            edges[richer[i][0]].push_back(richer[i][1]);
            indegree[richer[i][1]]++;
            if(quiet[result[richer[i][0]]]<quiet[result[richer[i][1]]])
                result[richer[i][1]] = result[richer[i][0]];
        }

        // populate the priority queue for nodes with Indegree count > 0
        for(int i=0;i<n;i++)
        {
            if(indegree[i]>0)
                pQ.push(make_pair(indegree[i],i));  //pair {indegree count, node}
        }

        int node;

        // process all nodes in priortiy queue
        while(!pQ.empty())
        {            
            node = pQ.top().second;
            pQ.pop();

            // update result of all neighbor nodes if current node's quiteness is lower
            for(int j=0;j<edges[node].size();j++)
            {
                if(quiet[result[node]]<quiet[result[edges[node][j]]])
                    result[edges[node][j]] = result[node];
            }
        }

        return result;                                   
    }
};
```

