class Solution {
    int[] res;
    public int[] loudAndRich(int[][] richer, int[] quiet) {
        HashMap<Integer, List<Integer>> map = new HashMap<>();
        for (int[] row : richer) {
            map.putIfAbsent(row[1], new ArrayList<Integer>());
            map.get(row[1]).add(row[0]);
        }
        res = new int[quiet.length];
        Arrays.fill(res, -1);
        for (int i = 0; i < quiet.length; i++) {
            res[i] = dfs(map, i, quiet);
        }
        return res;
    }

    private int dfs(HashMap<Integer, List<Integer>> adj, int i, int[] quiet) {
        if (res[i] != -1) return res[i];
        List<Integer> list = adj.get(i);
        res[i] = i;
        if (adj.get(i) != null) {
            for (int elem : adj.get(i)) {
                int n = dfs(adj, elem, quiet);
                if (quiet[n] < quiet[res[i]]) res[i] = n;
            }
        }
        return res[i];
    }
}
