class Solution {
public:
    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) {
        int n = quiet.size();
        int c = richer.size();
        vector<int> adj[n], rev[n], indegree(n, 0), dup, m[n], ans(n);
        for(int i = 0; i < c; i++){
            int a = richer[i][0];
            int b = richer[i][1];
            adj[a].push_back(b);
            rev[b].push_back(a);
            indegree[b]++;
        }
        dup = indegree;
        queue<pair<int, int>> q;
        for(int i = 0; i < n; i++){
            if(indegree[i] == 0){
                q.push({i, 0});
            }
        }
        while(!q.empty()){
            int temp = q.front().first;
            int lvl = q.front().second;
            q.pop();
            m[lvl].push_back(quiet[temp]);
            for(auto it: adj[temp]){
                indegree[it]--;
                if(indegree[it] == 0){
                    q.push({it, lvl+1});
                }
            }
        }
        map<int, int> mp;
        for(int i = 0; i < n; i++){
            mp[quiet[i]] = i;
        }
        for(int i = 0; i < n; i++){
            if(m[i].size() == 0) break;
            for(auto it: m[i]){
                int c = mp[it];
                if(rev[c].size() == 0){ 
                    ans[c] = c;
                }
                else{
                    int mini = quiet[c];
                    for(auto it2: rev[c]){
                        mini = min(mini, quiet[ans[it2]]);
                    }
                    ans[c] = mp[mini];
                }
            }
        }
        return ans;
    }
};
