class Solution {
public:
    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) {
        int V=quiet.size();
        vector<int> adj[V];
        vector<int> vis(V,0);
        vector<int> indegree(V,0);

        for(auto it:richer){
            adj[it[0]].push_back(it[1]);
            indegree[it[1]]++;
        }

        vector<int> ans(V);
        queue<int> q;
        for(int i=0;i<V;i++){
            // initializing ans of each node with node value.
            ans[i]=i;
            if(indegree[i]==0){
                q.push(i);
            }
        }

        while(!q.empty()){
            int node=q.front();
            q.pop();

            for(auto it: adj[node]){
                indegree[it]--;

                // Updating the answer value of child node if the quiet value of child node is more than parent node.
               if(quiet[ans[node]] < quiet[ans[it]]){
                    ans[it]=ans[node];
                }
                if(indegree[it]==0)q.push(it);
            }
        }



        return ans;

    }
};

