class Solution {
    public int[] loudAndRich(int[][] richer, int[] quiet) {
        int[] quietest = new int[quiet.length];
        for(int i = 0; i < quiet.length; i++){
            quietest[i] = searchForQuietest(richer, quiet, i, quietest);
        }
        return quietest;
