class Solution {
public:
    int n;
    vector<int> quiet;
    vector<vector<int>> adjList;
    vector<bool> isVisited;
    vector<int> result;

    int dfs(int curr) {
        isVisited[curr] = true;
        int minV = curr;

        for (int i : adjList[curr]) {
            if (!isVisited[i]) {
                int temp = dfs(i);
                if (quiet[minV] > quiet[temp]) {
                    minV = temp;
                }
            } else {
                if (quiet[minV] > quiet[result[i]]) {
                    minV = result[i];
                }
            }
        }

        result[curr] = minV;
        return minV;
    }

    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) {
        n = quiet.size();
        result.assign(n, 0);
        adjList.assign(n, vector<int>(0));
        isVisited.assign(n, false);
        this->quiet = quiet;

        for (vector<int> edge : richer) {
            adjList[edge[1]].push_back(edge[0]);
        }

        for (int i=0 ; i<n ; i++) {
            if (!isVisited[i])
                dfs(i);
        }

        return result;
    }
};
