class Solution {
public:
    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) 
    {
        int n = quiet.size();
        vector<int> indegree(n,0),ans(n);
        vector<vector<int>> g(n);
        queue<int> leaves;

        for (auto& x:richer)
        {
            g[x[0]].push_back(x[1]);
            indegree[x[1]]++;
        }

        for (int i=0;i<n;i++)
        {
            if (indegree[i]==0)
                leaves.push(i);
            ans[i]=i;
        }

        while (!leaves.empty())
        {
            int sz=leaves.size();
            for (int i=0;i<sz;i++)
            {
                int x = leaves.front();
                leaves.pop();

                for (auto& neighbour:g[x])
                {
                    indegree[neighbour]--;
                    if (quiet[ans[neighbour]]>quiet[ans[x]])
                        ans[neighbour]=ans[x];
                    if (indegree[neighbour]==0)
                        leaves.push(neighbour);
                }
            }
        }
        return ans;
    }
};
