class Solution {
    public int[] loudAndRich(int[][] richer, int[] quiet) {
        int n = quiet.length;
        // construct "adjacent list" , record richer people 
        List<List<Integer>> list = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            list.add(new ArrayList<>());
        }
        for (int i = 0; i < richer.length; i++) {
            list.get(richer[i][1]).add(richer[i][0]);
        }

        int[] result = new int[n];
        for (int i = 0; i < n; i++) {
            // no one is richer than i
            if (list.get(i).size() == 0) {
                result[i] = i;
                continue;
            }
            // otherwise,do BFS
            result[i] = bfs(list, quiet, i);
        }

        return result;
    }
    private int bfs(List<List<Integer>> list, int[] quiet, int index) {
        int result = index;
        int q = Integer.MAX_VALUE; // least quiet
        boolean[] visited = new boolean[quiet.length];
        Queue<Integer> queue = new LinkedList<>();
        visited[index] = true;
        queue.offer(index);

        while (!queue.isEmpty()) {
            int curr = queue.poll();
            if (quiet[curr] < q) {
                q = quiet[curr];
                result = curr;
            }
            if (list.get(curr).size() != 0) {
                for (int next : list.get(curr)) {
                    if (visited[next]) {
                        continue;
                    }
                    queue.offer(next);
                    visited[next] = true;
                }
            }
        }
        return result;
    }
}
