class Solution {
    public int[] loudAndRich(int[][] richer, int[] quiet) {
        int N = quiet.length;
        int[][] graph = new int[N][N];
        for (int[] r : richer) {
            graph[r[1]][r[0]] = 1;
        }
        int[] result = new int[N];
        for (int i = 0; i < N; i++) {
            result[i] = -1;
        }
        for (int i = 0; i < N; i++) {
            dfs(graph, quiet, result, i);
        }
        return result;
    }
    private int dfs(int[][] graph, int[] quiet, int[] result, int index) {
        if (result[index] > -1) {
            return result[index];
        }
        int r = index;
        for (int i = 0; i < quiet.length; i++) {
            if (graph[index][i] == 1) {
                int temp = dfs(graph, quiet, result, i);
                if (quiet[temp] < quiet[r]) {
                    r = temp;
                }
            }
        }
        result[index] = r;
        return result[index];
    }
}
