            if (!visited[i])
                dfs(i, visited, adj, order);

        while (!order.empty()) {
            int node = order.top();
            order.pop();
            for (auto child : adj[node]) {
                if (quiet[ans[child]] > quiet[ans[node]])
                    ans[child] = ans[node];
            }
        }
        return ans;
    }
};
