class Solution:
    def loudAndRich(self, richer: List[List[int]], quiet: List[int]) -> List[int]:
        N = len(quiet)
        graph = collections.defaultdict(list)
        for e in richer:
            pre = e[1]
            next = e[0]
            graph[pre].append(next)

        @cache
        def dfs(root):
            ret = quiet[root]
            for next in graph[root]:
                ret = min(ret, dfs(next))
            return ret

        hm = dict()
        for i, e in enumerate(quiet):
            hm[e] = i

        ans = []
        for i in range(N):
            ans.append(hm[dfs(i)])
        return ans
