        visited[s]=true;
        int min=s;
        for(int x:adj[s]){
            if(!visited[x])
            dfs(x,adj,visited,ans,quiet);
            if(quiet[ans[x]]<quiet[min])
            min=ans[x];
        }
        ans[s]=min;
    }
