class Solution {
public:
    vector<int>ans;
    unordered_map<int,vector<int>>adj;

    int dfs(int node, vector<int>& quiet){

        if(ans[node] != (-1)){return ans[node];} //caching 

         <!-- step2 -->
        int mini = node;
        <!-- step3 -->
        for(auto it : adj[node]){

            if(quiet[dfs(it,quiet)] < quiet[mini]){

               mini = dfs(it,quiet);

            }


        }

        ans[node] = mini;
        return mini;

    }
    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) {
        int n =  quiet.size();
        ans.resize(n,-1);
        <!-- step1 -->
        for(auto it : richer ){
            adj[it[1]].push_back(it[0]);
        }
        for(int i=0 ; i<n ; i++){
            if(ans[i] == -1){
                dfs(i,quiet);
            }
        }
        return ans;

    }
};
