from functools import cache
from itertools import chain


class Solution:
    def loudAndRich(self, richer: list[list[int]], quiet: list[int]) -> list[int]:
        n = len(quiet)

        g = self.create_graph(richer)

        @cache
        def dfs(u: int) -> tuple[int, int]:
            """
            finds the least quiet person having money more than or equal to person "u" 
            and returns tuple containing the quietness and corresponding person index 
            with that quietness 
            :param u: 
            :return:  
            """
            return min(
                chain(
                    map(dfs, g[u]),
                    ((quiet[u], u),)
                )
            )

        return [dfs(i)[1] for i in range(n)]

    @staticmethod
    def create_graph(edges: list[list[int]]) -> defaultdict[int, list[int]]:
        g = defaultdict(list)

        for a, b in edges:
            g[b].append(a)

        return g
