class Solution {
    public int[] loudAndRich(int[][] richer, int[] quiet) {
        return dfsApproach(richer, quiet);
    }

    /*
    Algo:
        Create graph for showing poor relationship
        graph = {k: richer[1], v: list(richer[0])}

        run dfs and get node with min quietness for each node
    */

    private int[] dfsApproach(int[][] richer, int[] quiet) {
        Map<Integer, Set<Integer>> graph = Arrays.stream(richer).collect(Collectors.groupingBy(rich -> rich[1], Collectors.mapping(rich -> rich[0], Collectors.toSet())));

        int n = quiet.length;
        int[] result = IntStream.generate(() -> -1).limit(n).toArray();

        IntStream.range(0, n).forEach(u -> dfsApproach(u, graph, quiet, result));

        return result;
    }
    private int dfsApproach(int u, Map<Integer, Set<Integer>> graph, int[] quiet, int[] result) {
        if(result[u] != -1) return result[u];

        Set<Integer> neighbors = graph.getOrDefault(u, Set.of());

        return result[u] = neighbors.stream().mapToInt(v -> dfsApproach(v, graph, quiet, result)).reduce(u, (minNode, v) -> quiet[minNode] < quiet[v] ? minNode : v);
    }

}
```

