class Solution {
public:
    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) {
        int n = quiet.size();
        vector<vector<int>> adj(n);
        vector<int> ans(n, INT_MAX);
        vector<int> indegree(n, 0);
        queue<int> q;
            for (const auto& relation : richer) {
            int source = relation[0];
            int target = relation[1];
            adj[source].push_back(target);
            indegree[target]++;
        }
        for (int i = 0; i < n; i++) {
            ans[i] = i;
            if (indegree[i] == 0) {
                q.push(i);
            }
        }

        while (!q.empty()) {
            int node = q.front();
            q.pop();

            for (int neighbor : adj[node]) {
                if (ans[neighbor] == INT_MAX || quiet[ans[neighbor]] > quiet[ans[node]]) {
                    ans[neighbor] = ans[node];
                }
                indegree[neighbor]--;
                if (indegree[neighbor] == 0) {
                    q.push(neighbor);
                }
            }
        }

        return ans;
    }
};

```

