class Solution {
public:
    int dfs(int idx, vector<int> &ans, vector<int> adj[], vector<int> &quiet) {
        if(ans[idx] == -1) {
            ans[idx] = idx;
            for(auto &it:adj[idx]) {
                int quietest = dfs(it, ans, adj, quiet);
                if(quiet[quietest] < quiet[ans[idx]]) {
                    ans[idx] = quietest;
                }
            }
        }
        return ans[idx];

    }
    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) {
        int n = quiet.size();
        vector<int> adj[n];
        for(auto &it : richer) {
            adj[it[1]].push_back(it[0]);
        }
        vector<int> ans(n, -1);
        for(int i=0;i<n;i++) {
            dfs(i, ans, adj, quiet);
        }
        return ans; 
    }
};
