class Solution:
    def loudAndRich(self, richer: List[List[int]], quiet: List[int]) -> List[int]:
      n = len(quiet) 
      g,ans,mark = [[] for _ in range(n)],[i for i in range(n)],[False]*n
      for x,y in richer : g[y].append(x) 

      def dfs(x):
        mark[x] = True 
        for i in g[x]:
          if not mark[i] : dfs(i) 
          if quiet[x] > quiet[i] : 
            quiet[x] = quiet[i] # taking min of all the parent values 
            ans[x] = ans[i]  # taking index which has min value

      for i in range(n): 
        if not mark[i] : 
          dfs(i) 

      return ans 

