from collections import defaultdict,deque

class Solution:
    def loudAndRich(self, richer: List[List[int]], quiet: List[int]) -> List[int]:

        graph,revg = defaultdict(list),defaultdict(list)
        n = len(quiet)
        ind = [0 for i in range(n)]

        for x,y in richer:
            graph[x].append(y)
            revg[y].append(x)
            ind[y]+=1

        q = deque()

        for i,x in enumerate(ind):
            if x==0:
                q.append(i)

        ans = [0 for i in range(n)]
        while q:
            cs = len(q)
            for i in range(cs):
                ele = q.popleft()
                ans[ele] = ele
                cq = quiet[ele]

                for anc in revg[ele]:
                    if quiet[ans[anc]]<cq:
                        cq = quiet[ans[anc]]
                        ans[ele]=ans[anc]

                for neigh in graph[ele]:
                    ind[neigh]-=1
                    if ind[neigh]==0:
                        q.append(neigh)

        return ans




