public class Solution {
    public int[] LoudAndRich(int[][] richer, int[] quiet) {
        var dict = new Dictionary<int, IList<int>>();
        for (int i = 0; i < richer.Length; i++)
        {
            if (!dict.ContainsKey(richer[i][1]))
                dict[richer[i][1]] = new List<int>();
            dict[richer[i][1]].Add(richer[i][0]);
        }
        int[] res = new int[quiet.Length];
        for (int i = 0; i < res.Length; i++) res[i] = -1;
        for (int i = 0; i < quiet.Length; i++)
            Helper(i, dict, quiet, res);
        return res;
    }
    void Helper(int index, Dictionary<int, IList<int>> dict, int[] quiet, int[] res)
    {
        if (res[index] > -1) return;
        res[index] = index;
        if (dict.ContainsKey(index))
        {
            foreach(var item in dict[index])
            {
                Helper(item, dict, quiet, res);
                if (quiet[res[item]] < quiet[res[index]])
                    res[index] = res[item];
            }           
        }
    }
}
