                dfs(graph,graph[node][i],quiet,answer);
                if(quiet[answer[node]] > quiet[answer[graph[node][i]]])
                {
                    answer[node] = answer[graph[node][i]]; //comparing all the answers from child nodes( nodes which are richer ).
                }
            }
        }
    }
public:
    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) {
        int n = quiet.size();
        vector<vector<int>>graph(n);
        for(int i=0;i<richer.size();i++)
        {
            graph[richer[i][1]].push_back(richer[i][0]);
        }
        vector<int>answer(n,-1);
        for(int i=0;i<n;i++)
        {
            if(answer[i] == -1)
            {
                dfs(graph,i,quiet,answer);
