class Solution:
    def loudAndRich(self, richer: List[List[int]], quiet: List[int]) -> List[int]:

        """
        0->1->2
         ->1->3->4  
         ->7->3->5
              3->6
        """
        answer = []
        visited = set() 
        graph = collections.defaultdict(list)
        def prepareGraph():

            for richEntry in richer:
                graph[richEntry[1]].append(richEntry[0])

        def dfs(node):

            visited.add(node)

            for entry in graph[node]:
                if entry not in visited:
                    dfs(entry)
                # while calling dfs, comparing the child node to the root node and replace if quiteness is minimum
                if quiet[answer[entry]]< quiet[answer[node]]:
                    answer[node] = answer[entry]

        prepareGraph()
        answer = [i for i in range(len(quiet))]

        for key in range(len(quiet)):
            dfs(key)
        return answer


