class Solution {
public:
    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) {
        int n = quiet.size() ;
        vector<int> ans(n) ;
        for(int i=0;i<n;i++) ans[i] = i ;  
        for(int i=0 ;i<n ; i++){
            bool change = false;  
            for(auto e : richer){
                if(quiet[e[0]] < quiet[e[1]]){
                    quiet[e[1]] = quiet[e[0]] ;
                    ans[e[1]] = ans[e[0]] ; 
                    change = true ; 
                }
            }
            if(change == false) break;  
        }

        return ans ; 

    }
};
