class Solution {
    public int[] loudAndRich(int[][] richer, int[] quiet) {
        //cc 
        if (richer == null) return new int[0];

        int n = quiet.length;
        int[] res = new int[n];

        // richer and more quiet people add to the result

        List<List<Integer>> graph = new ArrayList<>();
        for (int i = 0; i < n; i++) graph.add(new ArrayList<>());
        int[] indeg = new int[n];

        for (int[] rich : richer) {
            graph.get(rich[0]).add(rich[1]);
            indeg[rich[1]]++;
        }

        Queue<Integer> queue = new LinkedList<>();
        for (int i = 0; i < n; i++) {
            if (indeg[i] == 0) queue.offer(i);
            res[i] = i;
        }

        while (!queue.isEmpty()) {
            int curr = queue.poll(); //richer

            for (int next : graph.get(curr)) {
                indeg[next]--;

                if (quiet[res[next]] > quiet[res[curr]]) {
                    res[next] = res[curr];
                }

                if (indeg[next] == 0) queue.offer(next);
            }
        }
        return res;
    }
}

//T:O(m + n) S:O(m + n)
// n -- number of people
// m -- number of richer people
