class Solution {
public:
    void traverse(vector<vector<int>>& graph,int guy,vector<int> &ans,int minQ,vector<int>& quiet){

        if(ans[guy]!=-1&&quiet[ans[guy]]<=quiet[minQ])return;
        if(quiet[guy]<quiet[minQ])minQ = guy;

        if(ans[guy]==-1||quiet[minQ]<quiet[ans[guy]])ans[guy] = minQ;

        for(auto nextGuy: graph[guy]){
            traverse(graph,nextGuy,ans,minQ,quiet);
        }
    }
    vector<int> loudAndRich(vector<vector<int>>& richer, vector<int>& quiet) {
        // make a graph and it will be a tree.
        // richest person at top and follows
        // make a directed graph
        // run dfs and carry the quiter guy 

        int guys = quiet.size();
        vector<vector<int>> graph(guys);
        //no need to maintain visited array as it is directed graph and there is no cycle
        //x > y and y > z then x > z definetely not z > x (therefore no cycles)

        for(int ind = 0; ind < richer.size(); ind++){
            int guy1 = richer[ind][0];
            int guy2 = richer[ind][1];

            graph[guy1].push_back(guy2);
        }

        vector<int> ans(guys,-1);
        set<pair<int,int>> sguys;
        for(int ind = 0; ind < guys; ind++){
            sguys.insert({quiet[ind],ind});
        }
        for(auto guy: sguys){
            traverse(graph,guy.second,ans,guy.second,quiet);
        }
        return ans;
    }
};
```

